mapper.select:
select *
from
(
	select rownum as RNUM,A.*
	from 
	(
		select * from addrbook
		order by bookNo desc
	)A
)

where RNUM > #{firstRecordIndex}
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update addrbook
set name=#{name}, company_name=#{company_name}, deptName=#{deptName}, posName=#{posName},
	email=#{email},	hp=#{hp}, corp_phone=#{corp_phone},
	address=#{address},addressDetail=#{addressDetail}
where bookNo=#{bookNo}
