mapper.select:
select * from c_board
where community_No=#{communityNo}
order by board_No

mapper.update:
update c_board
set board_Name=#{boardName},board_master=#{boardMaster},
intro=#{intro}, publicSet=#{publicSet}
where board_No=#{boardNo}

mapper.select:
select * from c_boardView
where community_No=#{communityNo}
order by regdate desc

select * 
from
(
	select rownum as RNUM, A.* 
	from
	(
		select c.*, (sysdate-regdate)*24 as newImgTerm
		from c_BoardContent c
		
		order by group_No desc, sortno;
	)A
)

where RNUM > #{firstRecordIndex} 
and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
 	update c_boardContent
	set readcounts=readcounts+1
	where content_No=#{contentNo}
